}; на #[rtic::app(/* .. */)]
mod app { use some_crate::some_func; fn func() { some_crate::some_func(); }
} или use some_crate::some_func; #[rtic::app(/* .. */)]
mod app { fn func() { super::some_crate::some_func(); }
}

