} #[local]
struct Local { local_to_b: i64,
} #[task(shared = [shared_by_a_and_b])]
fn a(_: a::Context) {} #[task(shared = [shared_by_a_and_b], local = [local_to_b])]
fn b(_: b::Context) {}

