// ^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^
const APP: () = { #[init] fn init(cx: init::Context) { cx.core.DWT.enable_cycle_counter(); // опционально, настройка запуска DWT без подключенного отладчика cx.core.DCB.enable_trace(); } #[task(schedule = [b])] fn a(cx: a::Context) { // .. }
